using System;
using System.Collections.Generic;
using System.Threading.Tasks;

class Program
{
    
    static void Main()
    {
        Console.OutputEncoding = System.Text.Encoding.UTF8;
       

        Console.WriteLine("---Консольный кликер");
        while (true)
        {
            Console.WriteLine("Меню взаимодействия");
            Console.WriteLine("K - Кликать");
            Console.WriteLine("P - Профиль");
            Console.WriteLine("Y - Магазин ASCII");
            Console.WriteLine("E - Апгрейды");
            Console.WriteLine("Q - Выход");

            Console.WriteLine("Введите операцию: ");
            var key = Console.ReadKey(true).Key;

            if (key == ConsoleKey.K)
            {
                RunClicker();
            }
            else if (key == ConsoleKey.E)
            {
                RunUpgrades();
            }
            else if (key == ConsoleKey.P)
            {
                ProfileMenu();
            }
            else if (key == ConsoleKey.Q)
            {
                Console.WriteLine("\nВыходим....");
                return;
            }
            else if (key == ConsoleKey.Y)
            {
                RunASCIIStore();
            }
            else
            {
                Console.WriteLine("\nНеверный ввод, повторите:\n");

            }
        }
    }



    static int resource = 10000;
    static int upgradeCost = 100;
    static int clickerPower = 1;
    static int incomePerClick = 0;
    static int costOfBuilding = 1000;
    static int quantityOfBuildings = 0;




    static void RunClicker()
    {
        Console.WriteLine("Нажимайте на 'С' чтобы получать энергию");
        Console.WriteLine("Нажмите на 'Q' чтобы выйти в основное меню");
        Console.WriteLine("Ввод");

        while (true)
        {

            var key = Console.ReadKey(true).Key;

            if (key == ConsoleKey.C)
            {
                resource += clickerPower;
                resource += incomePerClick;
                Console.WriteLine($"Энергия +{clickerPower}");
                if (quantityOfBuildings >= 1)
                {
                    Console.WriteLine($"Доход с фабрик + {incomePerClick}");
                }

                Console.WriteLine($"\nЭнергия: {resource}");

            }

            else if (key == ConsoleKey.Q)
            {
                break;
            }
        }
    }

    static void RunUpgrades()
    {
        Console.WriteLine("Меню улучшений:");
        Console.WriteLine($"\n\t\t[--- Улучшить клик (+2 к текущему клику) = {upgradeCost} энергии \t Покупка = 'A' ]");
        Console.WriteLine($"\n\t\t[--- Купить фабрику кликов (+10 к каждому клику) = {costOfBuilding} энергии \t Покупка = 'M' ]");
        Console.WriteLine("Выйти - нажмите 'Q'");
        while (true)
        {

            var key = Console.ReadKey(true).Key;

            if (resource >= upgradeCost && key == ConsoleKey.A)
            {
                clickerPower += 2;
                resource -= 100;

                Console.WriteLine($"Потрачено {upgradeCost} энегрии кликер теперь == {clickerPower}");
                upgradeCost = (int)(upgradeCost + 0.5 * upgradeCost);
            }
            else if (resource >= costOfBuilding && key == ConsoleKey.M)
            {
                resource -= costOfBuilding;
                incomePerClick += 10;
                quantityOfBuildings += 1;
                Console.WriteLine($"Потрачено {costOfBuilding} энергии теперь у вас == {quantityOfBuildings} фабрик");
                costOfBuilding = (int)(costOfBuilding + 0.5 * costOfBuilding);

            }
            else if (resource < upgradeCost && key == ConsoleKey.A)
            {
                Console.WriteLine($"Недостаточно ресурсов {resource} < {upgradeCost}");
                break;
            }
            else if (resource < upgradeCost && key == ConsoleKey.M)
            {
                Console.WriteLine($"Недостаточно ресурсов {resource} < {costOfBuilding}");
                break;
            }
            else if (key == ConsoleKey.Q)
            {
                return;
            }

        }

    }


    static void ProfileMenu()
    {

        Console.WriteLine("Нажмите 'Q' чтобы выйтив главное меню");
        Console.WriteLine($"Профиль \n Энергия - {resource} \n Сила клика - {clickerPower} \n Количество фабрик - {quantityOfBuildings}\n----------------------------------------");
        while (true)
        {
            Console.WriteLine("[Ваши арты:]");
            if (frirenKey > 0)
            {
                Console.WriteLine("\nТеперь вам доступен Friren!\nНажмите 'F1'чтобы посмотреть\n");
                
            }
            else if (luckyStarKey > 0)
            {
                Console.WriteLine("\nТеперь вам доступна Lucky Star!\nНажмите 'F2'чтобы посмотреть\n");
                
            }
            else if (remKey > 0) 
            {
                Console.WriteLine("\nТеперь вам доступна Rem!\nНажмите 'F3'чтобы посмотреть\n");
            }
            var key = Console.ReadKey(true).Key;
            if (key == ConsoleKey.F1 && frirenKey > 0)
            {
                RunASCIIFriren();
                Console.WriteLine("Нажмите Enter, чтобы вернуться...");
                Console.ReadLine();
                return;
            }
            else if (key == ConsoleKey.F2 && luckyStarKey > 0)
            {
                RunASCIILuckyStar();
                Console.WriteLine("Нажмите Enter, чтобы вернуться...");
                Console.ReadLine();
                return;
            }
            else if (key == ConsoleKey.F3 && remKey > 0)
            {
                RunASCIIRem();
                Console.WriteLine("Нажмите Enter, чтобы вернуться...");
                Console.ReadLine();
                return;
            }
            else if (key == ConsoleKey.Q) 
            {
                Console.WriteLine("Выходим....");
                return;
            } 
        }
    }
    static int frirenKey = 0;
    static int luckyStarKey = 0;
    static int remKey = 0;

    static void RunASCIIStore()
    {
        Console.WriteLine("ASCII арты: \n F1 - Friren [5000] энергии \n F2 - Lucky Star [7500] энергии \n F3 - Rem [10000] энергии");
        Console.WriteLine("Нажмите 'Q' чтобы выйти");
        Console.WriteLine("Выберите арт:\n Ввод:");
        while (true){

            var key = Console.ReadKey(true).Key;
            if (key == ConsoleKey.F1 && resource >= 5000)
            {
                frirenKey = 1;
                RunASCIIFriren();
            }
            else if (key == ConsoleKey.F2 && resource >= 7500)
            {
                luckyStarKey += 1;
                RunASCIILuckyStar();
            }
            else if (key == ConsoleKey.F3 && resource >= 10000)
            {
                remKey += 1;
                RunASCIIRem();
            }
            else if (key == ConsoleKey.Q) 
            {
                Console.WriteLine("Выходим.....");
            return;
            }
            else if (resource < 5000)
            {
                Console.WriteLine("У вас недостаточно энергии");
                return;
            }
        }
        
        
        
    }

